from Drizzle.Runtime import *## Behavior script: envEditorStart#class envEditorStart(LingoBehaviorScript):     def __init__(self):         super().__init__()            def exitframe(self):         cols = None        rows = None        l = None        self._global._movie.exitlock = LingoGlobal.TRUE        cols = self._movieScript.gLOprops.size.loch        rows = self._movieScript.gLOprops.size.locv        tmp_l=int(LingoNumber(1))        while tmp_l < LingoNumber(3):             l = LingoNumber(tmp_l)            self._movieScript.minilvleditdraw(l)
            tmp_l = int(l)            tmp_l += 1                    self._movieScript.gEnvEditButtons = LingoPropertyList(LingoSymbol("w"), LingoNumber(0),LingoSymbol("f"), LingoNumber(0))        self._movieScript.gLastEnvEditButtons = self._movieScript.gEnvEditButtons.duplicate()                return None            def checkkey(self, key):         rtrn = None        rtrn = LingoNumber(0)        self._movieScript.gEnvEditButtons[LingoGlobal.symbol(key)] = LingoGlobal.op_and(self._global._key.keypressed(key),LingoGlobal.op_ne(self._global._movie.window.sizestate,LingoSymbol("minimized")))        if (LingoGlobal.ToBool(self._movieScript.gEnvEditButtons[LingoGlobal.symbol(key)]) and LingoGlobal.op_eq_b(self._movieScript.gLastEnvEditButtons[LingoGlobal.symbol(key)], LingoNumber(0))):             rtrn = LingoNumber(1)                    self._movieScript.gLastEnvEditButtons[LingoGlobal.symbol(key)] = self._movieScript.gEnvEditButtons[LingoGlobal.symbol(key)]        return rtrn                    