from Drizzle.Runtime import *## Behavior script: envEditorStart#class envEditorStart(LingoBehaviorScript):     def __init__(self):         super().__init__()            def exitframe(self):         cols = None        rows = None        l = None        _global._movie.exitlock = LingoGlobal.TRUE        cols = _movieScript.gLOprops.size.loch        rows = _movieScript.gLOprops.size.locv        for tmp_l in LingoGlobal.pyrange(LingoNumber(1), LingoNumber(3)):             l = tmp_l            _movieScript.minilvleditdraw(l)
            tmp_l = l                    _movieScript.gEnvEditButtons = LingoPropertyList(w=LingoNumber(0),f=LingoNumber(0))        _movieScript.gLastEnvEditButtons = _movieScript.gEnvEditButtons.duplicate()                return None            def checkkey(self, key):         rtrn = None        rtrn = LingoNumber(0)        _movieScript.gEnvEditButtons[LingoGlobal.symbol(key)] = LingoGlobal.op_and(_global._key.keypressed(key),LingoGlobal.op_ne(_global._movie.window.sizestate,minimized))        if (LingoGlobal.ToBool(_movieScript.gEnvEditButtons[LingoGlobal.symbol(key)]) and LingoGlobal.op_eq_b(_movieScript.gLastEnvEditButtons[LingoGlobal.symbol(key)], LingoNumber(0))):             rtrn = LingoNumber(1)                    _movieScript.gLastEnvEditButtons[LingoGlobal.symbol(key)] = _movieScript.gEnvEditButtons[LingoGlobal.symbol(key)]        return rtrn                    