from Drizzle.Runtime import *## Behavior script: getExtraTile#class getExtraTile(LingoBehaviorScript):     def __init__(self):         super().__init__()            def change(self):         if LingoGlobal.op_eq_b(self.spritenum, LingoNumber(48)):             _movieScript.newSize[LingoNumber(1)] = _global.value(_global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(49)):             _movieScript.newSize[LingoNumber(2)] = _global.value(_global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(50)):             _movieScript.extraBufferTiles[LingoNumber(1)] = _global.value(_global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(51)):             _movieScript.extraBufferTiles[LingoNumber(2)] = _global.value(_global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(52)):             _movieScript.extraBufferTiles[LingoNumber(3)] = _global.value(_global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(53)):             _movieScript.extraBufferTiles[LingoNumber(4)] = _global.value(_global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(54)):             _movieScript.newSize[LingoNumber(3)] = _global.value(_global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(55)):             _movieScript.newSize[LingoNumber(4)] = _global.value(_global.sprite(self.spritenum).text)                            return None            