from Drizzle.Runtime import *## Behavior script: getExtraTile#class getExtraTile(LingoBehaviorScript):     def __init__(self):         super().__init__()            def change(self):         if LingoGlobal.op_eq_b(self.spritenum, LingoNumber(48)):             self._movieScript.newSize[LingoNumber(1)] = self._global.value(self._global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(49)):             self._movieScript.newSize[LingoNumber(2)] = self._global.value(self._global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(50)):             self._movieScript.extraBufferTiles[LingoNumber(1)] = self._global.value(self._global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(51)):             self._movieScript.extraBufferTiles[LingoNumber(2)] = self._global.value(self._global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(52)):             self._movieScript.extraBufferTiles[LingoNumber(3)] = self._global.value(self._global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(53)):             self._movieScript.extraBufferTiles[LingoNumber(4)] = self._global.value(self._global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(54)):             self._movieScript.newSize[LingoNumber(3)] = self._global.value(self._global.sprite(self.spritenum).text)                    elif LingoGlobal.op_eq_b(self.spritenum, LingoNumber(55)):             self._movieScript.newSize[LingoNumber(4)] = self._global.value(self._global.sprite(self.spritenum).text)                            return None            