from Drizzle.Runtime import *## Behavior script: massRenderLoop#class massRenderLoop(LingoBehaviorScript):     def __init__(self):         super().__init__()            def exitframe(self):         self._global._movie.exitlock = LingoGlobal.TRUE        if ((LingoGlobal.ToBool(self._global._key.keypressed(LingoNumber(56))) and LingoGlobal.ToBool(self._global._key.keypressed(LingoNumber(48)))) and LingoGlobal.op_ne_b(self._global._movie.window.sizestate, minimized)):             self._global._player.appminimize()        if LingoGlobal.ToBool(self._movieScript.checkexit()):             self._global._player.quit()        self._movieScript.gMassRenderL.deleteat(LingoNumber(1))
        if LingoGlobal.op_eq_b(self._movieScript.gMassRenderL.count, LingoNumber(0)):             self._global.alert("Mass Render Finished")
            self._global._movie.go(LingoNumber(1))        else:            self._global.put(LingoGlobal.concat_space("started rendering:",self._movieScript.gMassRenderL[LingoNumber(1)]))
            self._global.script("loadLevel").loadlevel(self._movieScript.gMassRenderL[LingoNumber(1)],LingoNumber(1))
            self._movieScript.gViewRender = LingoNumber(0)            self._global._movie.go(LingoNumber(42))                return None            