from Drizzle.Runtime import *## Behavior script: massRenderLoop#class massRenderLoop(LingoBehaviorScript):     def __init__(self):         super().__init__()            def exitframe(self):         _global._movie.exitlock = LingoGlobal.TRUE        if ((LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(56))) and LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(48)))) and LingoGlobal.op_ne_b(_global._movie.window.sizestate, minimized)):             _global._player.appminimize()        if LingoGlobal.ToBool(_movieScript.checkexit()):             _global._player.quit()        _movieScript.gMassRenderL.deleteat(LingoNumber(1))
        if LingoGlobal.op_eq_b(_movieScript.gMassRenderL.count, LingoNumber(0)):             _global.alert("Mass Render Finished")
            _global._movie.go(LingoNumber(1))        else:            _global.put(LingoGlobal.concat_space("started rendering:",_movieScript.gMassRenderL[LingoNumber(1)]))
            _global.script("loadLevel").loadlevel(_movieScript.gMassRenderL[LingoNumber(1)],LingoNumber(1))
            _movieScript.gViewRender = LingoNumber(0)            _global._movie.go(LingoNumber(42))                return None            