from Drizzle.Runtime import *## Behavior script: massRenderMenu#class massRenderMenu(LingoBehaviorScript):     def __init__(self):         super().__init__()            def exitframe(self):         pth = None        f = None        txt = None        q = None        up = None        dwn = None        lft = None        rgth = None        project = None        entr = None        if ((LingoGlobal.ToBool(self._global._key.keypressed(LingoNumber(56))) and LingoGlobal.ToBool(self._global._key.keypressed(LingoNumber(48)))) and LingoGlobal.op_ne_b(self._global._movie.window.sizestate, minimized)):             self._global._player.appminimize()        if LingoGlobal.ToBool(self._movieScript.checkexit()):             self._global._player.quit()        pth = LingoGlobal.concat(self._global.the_moviePath,"LevelEditorProjects",self._global.the_dirSeparator)        for tmp_f in self._movieScript.gLOADPATH:             f = tmp_f            pth = LingoGlobal.concat(pth,f,self._global.the_dirSeparator)                    txt = "Use arrow keys and space to select projects for rendering."        txt += str(LingoGlobal.RETURN)        for tmp_f in self._movieScript.gLOADPATH:             f = tmp_f            txt += str(LingoGlobal.concat(f,"/"))                    txt += str(LingoGlobal.RETURN)        txt += str(LingoGlobal.RETURN)        txt += str("Projects:")        txt += str(LingoGlobal.RETURN)        tmp_q=int(self._movieScript.ldPrps.listscrollpos)        while tmp_q < LingoGlobal.op_add(self._movieScript.ldPrps.listscrollpos,self._movieScript.ldPrps.listshowtotal):             q = tmp_q            if q > self._movieScript.projects.count:                 break                            else:                if LingoGlobal.op_ne_b(q, self._movieScript.ldPrps.currproject):                     if LingoGlobal.op_eq_b(self._movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,self._movieScript.projects[q])), LingoNumber(0)):                         txt += str(self._movieScript.projects[q])                                            else:                        txt += str(LingoGlobal.concat("*",self._movieScript.projects[q],"*"))                                                            elif LingoGlobal.op_eq_b(self._movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,self._movieScript.projects[q])), LingoNumber(0)):                     txt += str(LingoGlobal.concat_space("<",self._movieScript.projects[q],">"))                                    else:                    txt += str(LingoGlobal.concat_space("<*",self._movieScript.projects[q],"*>"))                                    txt += str(LingoGlobal.RETURN)                            tmp_q = q            tmp_q += 1                    self._global.member("ProjectsL").text = txt        txt = "MASS RENDER"        txt += str(LingoGlobal.RETURN)        txt += str(LingoGlobal.RETURN)        for tmp_q in self._movieScript.massRenderSelectL:             q = tmp_q            txt += str(q)            txt += str(LingoGlobal.RETURN)                    self._global.member("massRenderL").text = txt        up = self._global._key.keypressed(LingoNumber(126))        dwn = self._global._key.keypressed(LingoNumber(125))        lft = self._global._key.keypressed(LingoNumber(123))        rgth = self._global._key.keypressed(LingoNumber(124))        if LingoGlobal.op_eq_b(self._global._movie.window.sizestate, minimized):             up = LingoGlobal.FALSE            dwn = LingoGlobal.FALSE            lft = LingoGlobal.FALSE            rgth = LingoGlobal.FALSE                    if (LingoGlobal.ToBool(up) and LingoGlobal.op_eq_b(self._movieScript.ldPrps.lstup, LingoNumber(0))):             self._movieScript.ldPrps.currproject = LingoGlobal.op_sub(self._movieScript.ldPrps.currproject,LingoNumber(1))            if self._movieScript.ldPrps.currproject < LingoNumber(1):                 self._movieScript.ldPrps.currproject = self._movieScript.projects.count                                    if (LingoGlobal.ToBool(dwn) and LingoGlobal.op_eq_b(self._movieScript.ldPrps.lstdwn, LingoNumber(0))):             self._movieScript.ldPrps.currproject = LingoGlobal.op_add(self._movieScript.ldPrps.currproject,LingoNumber(1))            if self._movieScript.ldPrps.currproject > self._movieScript.projects.count:                 self._movieScript.ldPrps.currproject = LingoNumber(1)                                    if self._movieScript.ldPrps.currproject < self._movieScript.ldPrps.listscrollpos:             self._movieScript.ldPrps.listscrollpos = self._movieScript.ldPrps.currproject                    elif self._movieScript.ldPrps.currproject > LingoGlobal.op_add(self._movieScript.ldPrps.listscrollpos,self._movieScript.ldPrps.listshowtotal):             self._movieScript.ldPrps.listscrollpos = LingoGlobal.op_sub(self._movieScript.ldPrps.currproject,self._movieScript.ldPrps.listshowtotal)                    if ((LingoGlobal.ToBool(rgth) and LingoGlobal.op_eq_b(self._movieScript.ldPrps.rgth, LingoNumber(0))) and self._movieScript.projects.count > LingoNumber(0)):             if LingoGlobal.op_eq_b(LingoGlobal.chars(self._movieScript.projects[self._movieScript.ldPrps.currproject],LingoNumber(1),LingoNumber(1)), "#"):                 self.loadsubfolder(self._movieScript.projects[self._movieScript.ldPrps.currproject])                    elif (LingoGlobal.ToBool(lft) and LingoGlobal.op_eq_b(self._movieScript.ldPrps.lft, LingoNumber(0))):             if self._movieScript.gLOADPATH.count > LingoNumber(0):                 self._movieScript.gLOADPATH.deleteat(self._movieScript.gLOADPATH.count)
                self._global._movie.go(LingoNumber(4))                    self._movieScript.ldPrps.lstup = up        self._movieScript.ldPrps.lstdwn = dwn        self._movieScript.ldPrps.lft = lft        self._movieScript.ldPrps.rgth = rgth        if (LingoGlobal.ToBool(self._global._key.keypressed("A")) and LingoGlobal.op_ne_b(self._global._movie.window.sizestate, minimized)):             for tmp_project in self._movieScript.projects:                 project = tmp_project                if (LingoGlobal.op_eq_b(self._movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,project)), LingoNumber(0)) and LingoGlobal.op_ne_b(LingoGlobal.chars(project,LingoNumber(1),LingoNumber(1)), "#")):                     self._movieScript.massRenderSelectL.add(LingoGlobal.concat(pth,project))                                    elif (LingoGlobal.ToBool(self._global._key.keypressed("C")) and LingoGlobal.op_ne_b(self._global._movie.window.sizestate, minimized)):             self._movieScript.massRenderSelectL = LingoList()                    elif ((((LingoGlobal.ToBool(self._global._key.keypressed(LingoNumber(48))) and LingoGlobal.ToBool(self._global._key.keypressed("Z"))) and LingoGlobal.ToBool(self._global._key.keypressed("R"))) or LingoGlobal.ToBool(self._global._key.keypressed("1"))) and LingoGlobal.op_ne_b(self._global._movie.window.sizestate, minimized)):             self._global._movie.go(LingoNumber(9))        entr = LingoGlobal.op_and(self._global._key.keypressed(" "),LingoGlobal.op_ne(self._global._movie.window.sizestate,minimized))        if (LingoGlobal.ToBool(entr) and LingoGlobal.op_eq_b(self._movieScript.ldPrps.lstenter, LingoNumber(0))):             if LingoGlobal.op_eq_b(self._movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,self._movieScript.projects[self._movieScript.ldPrps.currproject])), LingoNumber(0)):                 self._movieScript.massRenderSelectL.add(LingoGlobal.concat(pth,self._movieScript.projects[self._movieScript.ldPrps.currproject]))            else:                self._movieScript.massRenderSelectL.deleteone(LingoGlobal.concat(pth,self._movieScript.projects[self._movieScript.ldPrps.currproject]))                    self._movieScript.ldPrps.lstenter = entr        if (LingoGlobal.ToBool(self._global._key.keypressed(LingoNumber(36))) and LingoGlobal.op_ne_b(self._global._movie.window.sizestate, minimized)):             self._movieScript.gViewRender = LingoNumber(1)            self._movieScript.gMassRenderL = self._movieScript.massRenderSelectL.duplicate()            self._movieScript.gMassRenderL.addat(LingoNumber(1),"DUMMY")
            self._global._movie.go(LingoNumber(90))        self._global.go(self._global.the_frame)        return None            def loadsubfolder(self, fldrname):         self._movieScript.gLOADPATH.add(LingoGlobal.chars(fldrname,LingoNumber(2),LingoGlobal.lengthmember_helper(fldrname)))
        self._global._movie.go(LingoNumber(4))        return None            