from Drizzle.Runtime import *## Behavior script: massRenderMenu#class massRenderMenu(LingoBehaviorScript):     def __init__(self):         super().__init__()            def exitframe(self):         pth = None        f = None        txt = None        q = None        up = None        dwn = None        lft = None        rgth = None        project = None        entr = None        if ((LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(56))) and LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(48)))) and LingoGlobal.op_ne_b(_global._movie.window.sizestate, minimized)):             _global._player.appminimize()        if LingoGlobal.ToBool(_movieScript.checkexit()):             _global._player.quit()        pth = LingoGlobal.concat(_global.the_moviePath,"LevelEditorProjects",_global.the_dirSeparator)        for tmp_f in _movieScript.gLOADPATH:             f = tmp_f            pth = LingoGlobal.concat(pth,f,_global.the_dirSeparator)                    txt = "Use arrow keys and space to select projects for rendering."        txt += str(LingoGlobal.RETURN)        for tmp_f in _movieScript.gLOADPATH:             f = tmp_f            txt += str(LingoGlobal.concat(f,"/"))                    txt += str(LingoGlobal.RETURN)        txt += str(LingoGlobal.RETURN)        txt += str("Projects:")        txt += str(LingoGlobal.RETURN)        for tmp_q in LingoGlobal.pyrange(_movieScript.ldPrps.listscrollpos, LingoGlobal.op_add(_movieScript.ldPrps.listscrollpos,_movieScript.ldPrps.listshowtotal)):             q = tmp_q            if q > _movieScript.projects.count:                 break                            else:                if LingoGlobal.op_ne_b(q, _movieScript.ldPrps.currproject):                     if LingoGlobal.op_eq_b(_movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,_movieScript.projects[q])), LingoNumber(0)):                         txt += str(_movieScript.projects[q])                                            else:                        txt += str(LingoGlobal.concat("*",_movieScript.projects[q],"*"))                                                            elif LingoGlobal.op_eq_b(_movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,_movieScript.projects[q])), LingoNumber(0)):                     txt += str(LingoGlobal.concat_space("<",_movieScript.projects[q],">"))                                    else:                    txt += str(LingoGlobal.concat_space("<*",_movieScript.projects[q],"*>"))                                    txt += str(LingoGlobal.RETURN)                            tmp_q = q                    _global.member("ProjectsL").text = txt        txt = "MASS RENDER"        txt += str(LingoGlobal.RETURN)        txt += str(LingoGlobal.RETURN)        for tmp_q in _movieScript.massRenderSelectL:             q = tmp_q            txt += str(q)            txt += str(LingoGlobal.RETURN)                    _global.member("massRenderL").text = txt        up = _global._key.keypressed(LingoNumber(126))        dwn = _global._key.keypressed(LingoNumber(125))        lft = _global._key.keypressed(LingoNumber(123))        rgth = _global._key.keypressed(LingoNumber(124))        if LingoGlobal.op_eq_b(_global._movie.window.sizestate, minimized):             up = LingoGlobal.FALSE            dwn = LingoGlobal.FALSE            lft = LingoGlobal.FALSE            rgth = LingoGlobal.FALSE                    if (LingoGlobal.ToBool(up) and LingoGlobal.op_eq_b(_movieScript.ldPrps.lstup, LingoNumber(0))):             _movieScript.ldPrps.currproject = LingoGlobal.op_sub(_movieScript.ldPrps.currproject,LingoNumber(1))            if _movieScript.ldPrps.currproject < LingoNumber(1):                 _movieScript.ldPrps.currproject = _movieScript.projects.count                                    if (LingoGlobal.ToBool(dwn) and LingoGlobal.op_eq_b(_movieScript.ldPrps.lstdwn, LingoNumber(0))):             _movieScript.ldPrps.currproject = LingoGlobal.op_add(_movieScript.ldPrps.currproject,LingoNumber(1))            if _movieScript.ldPrps.currproject > _movieScript.projects.count:                 _movieScript.ldPrps.currproject = LingoNumber(1)                                    if _movieScript.ldPrps.currproject < _movieScript.ldPrps.listscrollpos:             _movieScript.ldPrps.listscrollpos = _movieScript.ldPrps.currproject                    elif _movieScript.ldPrps.currproject > LingoGlobal.op_add(_movieScript.ldPrps.listscrollpos,_movieScript.ldPrps.listshowtotal):             _movieScript.ldPrps.listscrollpos = LingoGlobal.op_sub(_movieScript.ldPrps.currproject,_movieScript.ldPrps.listshowtotal)                    if ((LingoGlobal.ToBool(rgth) and LingoGlobal.op_eq_b(_movieScript.ldPrps.rgth, LingoNumber(0))) and _movieScript.projects.count > LingoNumber(0)):             if LingoGlobal.op_eq_b(LingoGlobal.chars(_movieScript.projects[_movieScript.ldPrps.currproject],LingoNumber(1),LingoNumber(1)), "#"):                 self.loadsubfolder(_movieScript.projects[_movieScript.ldPrps.currproject])                    elif (LingoGlobal.ToBool(lft) and LingoGlobal.op_eq_b(_movieScript.ldPrps.lft, LingoNumber(0))):             if _movieScript.gLOADPATH.count > LingoNumber(0):                 _movieScript.gLOADPATH.deleteat(_movieScript.gLOADPATH.count)
                _global._movie.go(LingoNumber(4))                    _movieScript.ldPrps.lstup = up        _movieScript.ldPrps.lstdwn = dwn        _movieScript.ldPrps.lft = lft        _movieScript.ldPrps.rgth = rgth        if (LingoGlobal.ToBool(_global._key.keypressed("A")) and LingoGlobal.op_ne_b(_global._movie.window.sizestate, minimized)):             for tmp_project in _movieScript.projects:                 project = tmp_project                if (LingoGlobal.op_eq_b(_movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,project)), LingoNumber(0)) and LingoGlobal.op_ne_b(LingoGlobal.chars(project,LingoNumber(1),LingoNumber(1)), "#")):                     _movieScript.massRenderSelectL.add(LingoGlobal.concat(pth,project))                                    elif (LingoGlobal.ToBool(_global._key.keypressed("C")) and LingoGlobal.op_ne_b(_global._movie.window.sizestate, minimized)):             _movieScript.massRenderSelectL = LingoList()                    elif ((((LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(48))) and LingoGlobal.ToBool(_global._key.keypressed("Z"))) and LingoGlobal.ToBool(_global._key.keypressed("R"))) or LingoGlobal.ToBool(_global._key.keypressed("1"))) and LingoGlobal.op_ne_b(_global._movie.window.sizestate, minimized)):             _global._movie.go(LingoNumber(9))        entr = LingoGlobal.op_and(_global._key.keypressed(" "),LingoGlobal.op_ne(_global._movie.window.sizestate,minimized))        if (LingoGlobal.ToBool(entr) and LingoGlobal.op_eq_b(_movieScript.ldPrps.lstenter, LingoNumber(0))):             if LingoGlobal.op_eq_b(_movieScript.massRenderSelectL.getpos(LingoGlobal.concat(pth,_movieScript.projects[_movieScript.ldPrps.currproject])), LingoNumber(0)):                 _movieScript.massRenderSelectL.add(LingoGlobal.concat(pth,_movieScript.projects[_movieScript.ldPrps.currproject]))            else:                _movieScript.massRenderSelectL.deleteone(LingoGlobal.concat(pth,_movieScript.projects[_movieScript.ldPrps.currproject]))                    _movieScript.ldPrps.lstenter = entr        if (LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(36))) and LingoGlobal.op_ne_b(_global._movie.window.sizestate, minimized)):             _movieScript.gViewRender = LingoNumber(1)            _movieScript.gMassRenderL = _movieScript.massRenderSelectL.duplicate()            _movieScript.gMassRenderL.addat(LingoNumber(1),"DUMMY")
            _global._movie.go(LingoNumber(90))        _global.go(_global.the_frame)        return None            def loadsubfolder(self, fldrname):         _movieScript.gLOADPATH.add(LingoGlobal.chars(fldrname,LingoNumber(2),LingoGlobal.lengthmember_helper(fldrname)))
        _global._movie.go(LingoNumber(4))        return None            