from Drizzle.Runtime import *## Behavior script: massRenderStart#class massRenderStart(LingoBehaviorScript):     def __init__(self):         super().__init__()            def exitframe(self):         pth = None        f = None        filelist = None        i = None        n = None        l = None        txt = None        q = None        _global._movie.exitlock = LingoGlobal.TRUE        if ((LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(56))) and LingoGlobal.ToBool(_global._key.keypressed(LingoNumber(48)))) and LingoGlobal.op_ne_b(_global._movie.window.sizestate, minimized)):             _global._player.appminimize()        if LingoGlobal.ToBool(_movieScript.checkexit()):             _global._player.quit()        pth = LingoGlobal.concat(_global.the_moviePath,_global.the_dirSeparator,"LevelEditorProjects")        for tmp_f in _movieScript.gLOADPATH:             f = tmp_f            pth = LingoGlobal.concat(pth,_global.the_dirSeparator,f)                    _movieScript.projects = LingoList()        filelist = LingoList()        for tmp_i in LingoGlobal.pyrange(LingoNumber(1), LingoNumber(300)):             i = tmp_i            n = _global.getnthfilenameinfolder(pth,i)            if LingoGlobal.op_eq_b(n, LingoGlobal.EMPTY):                 break                            if LingoGlobal.op_ne_b(LingoGlobal.charof_helper(LingoGlobal.op_sub(LingoGlobal.lengthmember_helper(n),LingoNumber(3)),n), "."):                 _movieScript.projects.add(LingoGlobal.concat("#",n))            else:                filelist.append(n)            tmp_i = i                    for tmp_l in filelist:             l = tmp_l            if LingoGlobal.op_eq_b(LingoGlobal.chars(l,LingoGlobal.op_sub(LingoGlobal.lengthmember_helper(l),LingoNumber(3)),LingoGlobal.lengthmember_helper(l)), ".txt"):                 _movieScript.projects.add(LingoGlobal.chars(l,LingoNumber(1),LingoGlobal.op_sub(LingoGlobal.lengthmember_helper(l),LingoNumber(4))))                    txt = "Use the arrow keys to select a project. Use enter to open it."        txt += str(LingoGlobal.RETURN)        for tmp_f in _movieScript.gLOADPATH:             f = tmp_f            txt += str(LingoGlobal.concat(f,"/"))                    txt += str(LingoGlobal.RETURN)        txt += str(LingoGlobal.RETURN)        for tmp_q in _movieScript.projects:             q = tmp_q            txt += str(q)            txt += str(LingoGlobal.RETURN)                    _movieScript.ldPrps = LingoPropertyList(lstup=LingoNumber(1),lstDwn=LingoNumber(1),lft=LingoNumber(1),rgth=LingoNumber(1),lstenter=LingoNumber(0),currproject=LingoNumber(1),listscrollpos=LingoNumber(1),listshowtotal=LingoNumber(30))        _global.member("ProjectsL").text = txt        _global.member("PalName").text = "Press 'A' to select all in folder. Press 'C' to deselect all. Press ENTER to start rendering."                return None            