from Drizzle.Runtime import *## Behavior script: sliderBehav#class sliderBehav(LingoBehaviorScript):     def __init__(self):         super().__init__()            def mousewithin(self):         self._global.sprite(self.spritenum).color = self._global.color(LingoNumber(255),LingoNumber(0),LingoNumber(0))        if LingoGlobal.ToBool(self._global._mouse.mousedown):             val = LingoGlobal.op_sub(self._movieScript.restrict(self._global._mouse.mouseloc.loch,LingoNumber(50),LingoNumber(450)),LingoNumber(50))            self._global.sprite(self.spritenum).loch = LingoGlobal.op_add(val,LingoNumber(50))            match self._global.sprite(self.spritenum).member.name.lower():                 case "tileseedslider":                    self._movieScript.gLOprops.tileseed = val                    self._global.put("seed changed!")
                    self._global.the_randomSeed = self._movieScript.gLOprops.tileseed                                                        match self._global.sprite(self.spritenum).member.name.lower():             case "tileseedslider":                self._global.member("buttonText").text = LingoGlobal.concat_space("Tile random seed:",self._global.str(self._movieScript.gLOprops.tileseed))                                            return None            def mouseleave(self):         self._global.sprite(self.spritenum).color = self._global.color(LingoNumber(0),LingoNumber(0),LingoNumber(0))        self._global.member("buttonText").text = ""        self._global.sprite(LingoNumber(20)).quad = LingoList(LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)),LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)),LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)),LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)))                return None            