from Drizzle.Runtime import *## Behavior script: sliderBehav#class sliderBehav(LingoBehaviorScript):     def __init__(self):         super().__init__()            def mousewithin(self):         val = None        _global.sprite(self.spritenum).color = _global.color(LingoNumber(255),LingoNumber(0),LingoNumber(0))        if LingoGlobal.ToBool(_global._mouse.mousedown):             val = LingoGlobal.op_sub(_movieScript.restrict(_global._mouse.mouseloc.loch,LingoNumber(50),LingoNumber(450)),LingoNumber(50))            _global.sprite(self.spritenum).loch = LingoGlobal.op_add(val,LingoNumber(50))            match _global.sprite(self.spritenum).member.name.lower():                 case "tileseedslider":                    _movieScript.gLOprops.tileseed = val                    _global.put("seed changed!")
                    _global.the_randomSeed = _movieScript.gLOprops.tileseed                                                        match _global.sprite(self.spritenum).member.name.lower():             case "tileseedslider":                _global.member("buttonText").text = LingoGlobal.concat_space("Tile random seed:",_global.str(_movieScript.gLOprops.tileseed))                                            return None            def mouseleave(self):         _global.sprite(self.spritenum).color = _global.color(LingoNumber(0),LingoNumber(0),LingoNumber(0))        _global.member("buttonText").text = ""        _global.sprite(LingoNumber(20)).quad = LingoList(LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)),LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)),LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)),LingoGlobal.point(-LingoNumber(100),-LingoNumber(100)))                return None            