from Drizzle.Runtime import *## Movie script: LSlime#class MovieScript:     def __init__(self):         super().__init__()            def drfslimeapply(self, q, c):         q2 = None        c2 = None        effectr = None        tlpnt = None        sldmtrxloc = None        openareas = None        efmtrx = None        efrep = None        pixl = None        pixlrct = None        whiteloc = None        fc = None        d = None        lr = None        flgpnt = None        sld = None        deepeffect = None        imglr = None        mxctr = None        cntr = None        pnt = None        cl = None        ofst = None        lgt = None        nwlr = None        nwimg = None        pntrct = None        q2 = LingoGlobal.op_add(q,self.gRenderCameraTilePos.loch)        c2 = LingoGlobal.op_add(c,self.gRenderCameraTilePos.locv)        effectr = self.gEEprops.effects[self.r]        tlpnt = LingoGlobal.point(q2,c2)        sldmtrxloc = self.solidMtrx[q2][c2]        openareas = effectr.affectopenareas        efmtrx = effectr.mtrx[q2][c2]        efrep = effectr.repeats        pixl = LingoImage.Pxl        pixlrct = pixl.rect        whiteloc = _global.color(LingoNumber(255),LingoNumber(255),LingoNumber(255))        fc = LingoGlobal.op_add(openareas,LingoGlobal.op_mul(LingoGlobal.op_sub(LingoNumber(1.0000),openareas),self.solidafamv(tlpnt,LingoNumber(3))))        for tmp_d in LingoGlobal.pyrange(LingoNumber(1), LingoNumber(30)):             d = tmp_d            lr = LingoGlobal.op_sub(LingoNumber(30),d)            flgpnt = LingoGlobal.op_add(LingoGlobal.op_add(LingoNumber(1),LingoGlobal.op_gt(d,LingoNumber(9))),LingoGlobal.op_gt(d,LingoNumber(19)))            if (LingoGlobal.op_eq_b(lr, LingoNumber(9)) or LingoGlobal.op_eq_b(lr, LingoNumber(19))):                 sld = sldmtrxloc[flgpnt]                fc = LingoGlobal.op_add(openareas,LingoGlobal.op_mul(LingoGlobal.op_sub(LingoNumber(1.0000),openareas),self.solidafamv(tlpnt,flgpnt)))                            deepeffect = LingoNumber(0)            if (((LingoGlobal.op_eq_b(lr, LingoNumber(0)) or LingoGlobal.op_eq_b(lr, LingoNumber(10))) or LingoGlobal.op_eq_b(lr, LingoNumber(20))) or LingoGlobal.op_eq_b(sld, LingoNumber(0))):                 deepeffect = LingoNumber(1)                            imglr = _global.member(LingoGlobal.concat("layer",_global.str(lr))).image            mxctr = LingoGlobal.op_mul(LingoGlobal.op_mul(LingoGlobal.op_mul(LingoGlobal.op_mul(efmtrx,LingoGlobal.op_add(LingoNumber(0.2000),LingoGlobal.op_mul(LingoNumber(0.8000),deepeffect))),LingoNumber(0.0100)),efrep),fc)            for tmp_cntr in LingoGlobal.pyrange(LingoNumber(1), mxctr):                 cntr = tmp_cntr                if LingoGlobal.ToBool(deepeffect):                     pnt = LingoGlobal.op_add(LingoGlobal.op_mul(LingoGlobal.point(LingoGlobal.op_sub(q,LingoNumber(1)),LingoGlobal.op_sub(c,LingoNumber(1))),LingoNumber(20)),LingoGlobal.point(_global.random(LingoNumber(20)),_global.random(LingoNumber(20))))                                    elif LingoGlobal.op_eq_b(_global.random(LingoNumber(2)), LingoNumber(1)):                     pnt = LingoGlobal.op_add(LingoGlobal.op_mul(LingoGlobal.point(LingoGlobal.op_sub(q,LingoNumber(1)),LingoGlobal.op_sub(c,LingoNumber(1))),LingoNumber(20)),LingoGlobal.point(LingoGlobal.op_add(LingoNumber(1),LingoGlobal.op_mul(LingoNumber(19),LingoGlobal.op_sub(_global.random(LingoNumber(2)),LingoNumber(1)))),_global.random(LingoNumber(20))))                                    else:                    pnt = LingoGlobal.op_add(LingoGlobal.op_mul(LingoGlobal.point(LingoGlobal.op_sub(q,LingoNumber(1)),LingoGlobal.op_sub(c,LingoNumber(1))),LingoNumber(20)),LingoGlobal.point(_global.random(LingoNumber(20)),LingoGlobal.op_add(LingoNumber(1),LingoGlobal.op_mul(LingoNumber(19),LingoGlobal.op_sub(_global.random(LingoNumber(2)),LingoNumber(1))))))                                    cl = imglr.getpixel(pnt)                if LingoGlobal.op_ne_b(cl, whiteloc):                     ofst = LingoGlobal.op_sub(_global.random(LingoNumber(2)),LingoNumber(1))                    lgt = LingoGlobal.op_add(LingoNumber(3),_global.random(_global.random(_global.random(LingoNumber(6)))))                    if LingoGlobal.ToBool(self.effectIn3D):                         nwlr = self.drfget3dlr(lr)                                            else:                        nwlr = self.restrict(LingoGlobal.op_add(LingoGlobal.op_sub(lr,LingoNumber(1)),_global.random(LingoNumber(2))),LingoNumber(0),LingoNumber(29))                                            nwimg = _global.member(LingoGlobal.concat("layer",_global.str(nwlr))).image                    pntrct = LingoGlobal.rect(pnt,pnt)                    nwimg.copypixels(pixl,LingoGlobal.op_add(pntrct,LingoGlobal.rect(ofst,LingoNumber(0),LingoGlobal.op_add(LingoNumber(1),ofst),lgt)),pixlrct,LingoPropertyList(dict(color = cl)))
                    if LingoGlobal.op_eq_b(_global.random(LingoNumber(2)), LingoNumber(1)):                         nwimg.copypixels(pixl,LingoGlobal.op_add(pntrct,LingoGlobal.rect(LingoGlobal.op_add(ofst,LingoNumber(1)),LingoNumber(1),LingoGlobal.op_add(LingoNumber(2),ofst),LingoGlobal.op_sub(lgt,LingoNumber(1)))),pixlrct,LingoPropertyList(dict(color = cl)))                    else:                        nwimg.copypixels(pixl,LingoGlobal.op_add(pntrct,LingoGlobal.rect(LingoGlobal.op_sub(ofst,LingoNumber(1)),LingoNumber(1),ofst,LingoGlobal.op_sub(lgt,LingoNumber(1)))),pixlrct,LingoPropertyList(dict(color = cl)))                                    tmp_cntr = cntr                            tmp_d = d                            return None            def drfget3dlr(self, lr):         nwlr = None        nwlr = self.restrict(LingoGlobal.op_add(LingoGlobal.op_sub(lr,LingoNumber(2)),_global.random(LingoNumber(3))),LingoNumber(0),LingoNumber(29))        if (LingoGlobal.op_eq_b(lr, LingoNumber(6)) and LingoGlobal.op_eq_b(nwlr, LingoNumber(5))):             nwlr = LingoNumber(6)                    elif (LingoGlobal.op_eq_b(lr, LingoNumber(5)) and LingoGlobal.op_eq_b(nwlr, LingoNumber(6))):             nwlr = LingoNumber(5)                    return nwlr                    